import React, { useState } from 'react';
import './App.css';
import SearchResults from '../SearchResults/SearchResults';
import Playlist from '../Playlist/Playlist';
import SearchBar from '../SearchBar/SearchBar';

function App() {
  const [searchResults, setSearchResults] = useState(
    [
      {
        name: 'Tropical Storm',
        artist: 'Kenny Erikson',
        album: 'Once Bitten',
        id: 1,
      },
      {
        name: "Blue Glue (Feelin' Fine)",
        artist: 'Kenny Erikson',
        album: 'Disastrous Living',
        id: 2,
      },
      {
        name: "Love Is Only A Feeling",
        artist: "The Darkness",
        album: "Permission To Land",
        id: 3,
      },
    ]
  );

  const [playlistName, setPlaylistName] = useState("Example Playlist Name");
  const [playlistTracks, setPlaylistTracks] = useState(
    [
      {
        name: "Example playlist name 1",
        artist: "Example playlist artist 1",
        album: "Example playlist album 1",
        id: 11,
      },
      {
        name: "Example playlist name 2",
        artist: "Example playlist artist 2",
        album: "Example playlist album 2",
        id: 22,
      },
    ]
  );

  function addTrack(track) {
    const existingTrack = playlistTracks.find((savedTrack) => savedTrack.id === track.id);
    const newTrack = playlistTracks.concat(track);
    if (existingTrack) {
      console.log("Track already in playlist!");
    } else {
      setPlaylistTracks(newTrack);
    }
  };

  function removeTrack(track) {
    const newList = playlistTracks.filter(tracks => {
      return tracks !== track;
    })
  }

  return (
    <div>
      <h1>Ja<span className="highlight">mmm</span>ing</h1>
      <div className="App">
        <SearchBar />
        <div className="App-playlist">

          <SearchResults 
            userSearchResults={searchResults}
            onAdd={addTrack}
          />

          <Playlist 
            playlistName={playlistName} 
            playlistTracks={playlistTracks}
          />
        </div>
      </div>
    </div>
  );
}

export default App;
